mapper.update:
update post
set title    = #{vo.title},
    content  = #{vo.content},
    category = #{vo.category},
    type     = #{vo.type}
where post_id = #{postId}

mapper.select:
select post_id
from post
where post_id = #{postId}
  and user_id = #{userId}

mapper.update:
update post
set views = views + 1
where post_id = #{postId}
