mapper.update:
update post
set title = #{vo.title},
    content = #{vo.content},
    private_flag = #{vo.privateFlag},
    category_id = #{vo.categoryId}
where post_id = #{postId}

mapper.select:
select post_id
from post
where post_id = #{postId}

mapper.delete:
update post
set delete_flag = 'Y'
where post_id = #{postId}
